package main

/*
#cgo CFLAGS: -I/usr/include/ep11 -I/usr/include/opencryptoki 
#include <stdint.h>
#include <ep11.h>
#include <openssl/evp.h>
#include <stdlib.h>

*/
import "C"
import "ep11go/ep11"
import (
	"fmt"
	"encoding/hex"
     	"os"
        "strconv"
)


func main() {
        if len(os.Args) != 3 {
                fmt.Fprintf(os.Stderr, "usage: %s <control-domain> <domain>\n", os.Args[0])
                os.Exit(1)
        }

        // 1) Control domain (e.g. "3.19")
        controlDomain := os.Args[1]

        // 2) Target domain (e.g. 16)
        domain64, err := strconv.ParseUint(os.Args[2], 10, 32)
	domain := uint32(domain64)
        if err != nil {
                fmt.Fprintf(os.Stderr, "invalid domain: %v\n", err)
                os.Exit(1)
        }

	target := ep11.HsmInit(controlDomain) 
	
	privadmin1hex:=""
	privadmin1Bytes, err := hex.DecodeString(privadmin1hex)


// **********************************************************************************************************************
// ZERORIZE DOMAIN
// **********************************************************************************************************************
	_ , err = ep11.AdminCommand(target,domain, C.XCP_ADM_DOM_ZEROIZE,nil,[][]byte{privadmin1Bytes})        
        if err != nil {    
            fmt.Println(err)
        } else {
	 fmt.Printf("Domain %d zerofied\n", domain)
	}
	 
}
